import {
  Entity,
  Column,
  PrimaryGeneratedColumn,
  CreateDateColumn,
  ManyToOne,
  OneToMany,
} from 'typeorm';
import { TutorialCategory } from '../../tutorial-category/entities/tutorial-category.entity';
import { Lesson } from '../../lesson/entities/lesson.entity';
import { User } from '../../user/entities/user.entity';

export enum TutorialType {
  FREE = 'Free',
  PAID = 'Paid',
}

@Entity()
export class Tutorial {
  @PrimaryGeneratedColumn('uuid')
  id: string;

  @Column()
  title: string;

  @Column()
  topicName: string;

  @Column()
  content: string;

  @Column({
    type: 'varchar',
    array: true,
    nullable: true,
  })
  keywords: string[];

  @ManyToOne(() => User, (tutor) => tutor.tutorial)
  tutor: User;

  @Column({
    default:
      'data:image/jpeg;base64,',
    nullable: true,
  })
  image: string | null;

  @Column({
    type: 'enum',
    enum: TutorialType,
  })
  status: TutorialType;

  @Column({
    type: 'numeric',
    nullable: true,
  })
  price: number | null;

  // Many tutorial can belong to one category
  @ManyToOne(() => TutorialCategory, (category) => category.tutorial)
  category: TutorialCategory;

  @OneToMany(() => Lesson, (lesson) => lesson.tutorial)
  lessons: Lesson[];

  @CreateDateColumn()
  created_at: Date;

  @CreateDateColumn()
  updated_at: Date;
}
